        "sections": [
          {
            "name": null,
            "components": [
              {
                "raw_text": "8 tablespoons butter, soft"
              },
              {
                "raw_text": "1 cup sugar"
              },
              {
                "raw_text": "1 egg"
              },
              {
                "raw_text": "½ teaspoon vanilla extract"
              },
              {
                "raw_text": "½ teaspoon salt"
              },
              {
                "raw_text": "1½ cup flour"
              },
              {
                "raw_text": "½ teaspoon red food coloring (or customized it with other colors)"
              },
              {
                "raw_text": "n/a"
              }
            ]
          }
        ],
        "instructions": [
          {
            "display_text": "In a large bowl, mix together butter and sugar until light and fluffy."
          },
          {
            "display_text": "Mix in egg, vanilla, and salt."
          },
          {
            "display_text": "Mix in flour."
          },
          {
            "display_text": "Make the dough into a ball and cut into two halves."
          },
          {
            "display_text": "Add food coloring to one half of the dough, working it in until evenly mixed."
          },
          {
            "display_text": "On a large cutting board covered in plastic wrap, use a rolling pin to roll out each piece of dough to make two 12x9-inch (30x22-cm) rectangles. Tip: Place wax paper over the dough while rolling to keep the dough from sticking to the rolling pin."
          },
          {
            "display_text": "Take the colored dough rectangle and place it over the other regular dough rectangle."
          },
          {
            "display_text": "Use the plastic wrap under the regular dough rectangle to help you roll the two rectangles up."
          },
          {
            "display_text": "Fill a flat dish or baking pan with sprinkles. Roll the dough around it until the outside is completely coated."
          },
          {
            "display_text": "Chill for at least 4 hours."
          },
          {
            "display_text": "Preheat the oven to 375°F (190°C)."
          },
          {
            "display_text": "Cut the dough into ¼-inch (6-mm) slices and place them on a cookie sheet."
          },
          {
            "display_text": "Bake for 8-10 minutes, until blonde."
          },
          {
            "display_text": "Enjoy!"
          }
        ],
