        "sections": [
          {
            "name": "Crust",
            "components": [
              {
                "raw_text": "½ cup butter"
              },
              {
                "raw_text": "5 tablespoons cocoa powder"
              },
              {
                "raw_text": "¼ cup sugar"
              },
              {
                "raw_text": "1 egg, beaten"
              },
              {
                "raw_text": "1 ¼ cup graham cracker crumbs"
              },
              {
                "raw_text": "1 cup sliced almonds, chopped"
              },
              {
                "raw_text": "½ flaked coconuts"
              }
            ]
          },
          {
            "name": "Middle layer",
            "components": [
              {
                "raw_text": "½ cup softened butter"
              },
              {
                "raw_text": "2 tablespoons custard powder"
              },
              {
                "raw_text": "1 tablespoon cinnamon"
              },
              {
                "raw_text": "1 teaspoon ground ginger"
              },
              {
                "raw_text": "½ teaspoon ground cloves"
              },
              {
                "raw_text": "½ teaspoon ground nutmeg"
              },
              {
                "raw_text": "5 teaspoons heavy cream"
              },
              {
                "raw_text": "1 ½ cups powdered sugar"
              },
              {
                "raw_text": "1 cup gingersnap crumbs"
              }
            ]
          },
          {
            "name": "Chocolate ganache",
            "components": [
              {
                "raw_text": "6 ounces chocolate"
              },
              {
                "raw_text": "2 tablespoons butter"
              },
              {
                "raw_text": "Coarse gingersnap crumbs"
              }
            ]
          }
        ],
        "instructions": [
          {
            "display_text": "In a medium bowl set over a medium pot of boiling water, melt the butter, cocoa powder, and sugar together."
          },
          {
            "display_text": "Add the egg and keep stirring for a minute until the mix thickens."
          },
          {
            "display_text": "Remove the bowl from the heat."
          },
          {
            "display_text": "Add the graham cracker crumbs, almonds, and coconut flakes."
          },
          {
            "display_text": "Mix well, then pour into an 8x8 inch (20x20 cm) pan and flatten to an even layer."
          },
          {
            "display_text": "In a large bowl, cream the softened butter, custard powder, cinnamon, ginger, cloves, nutmeg, and heavy cream together until smooth."
          },
          {
            "display_text": "Add the powdered sugar and gingersnap crumbs and mix until smooth."
          },
          {
            "display_text": "Spread mixture on top of the crust and in an even layer."
          },
          {
            "display_text": "Make the ganache: in a medium bowl set over a medium pot of boiling water, slowly melt the chocolate and the butter."
          },
          {
            "display_text": "Let the ganache cool a bit, then pour it over the gingersnap layer."
          },
          {
            "display_text": "Chill the bars for at least 3 hours."
          },
          {
            "display_text": "Cut the bars into 9 even squares and sprinkle with coarse gingersnap crumbs."
          },
          {
            "display_text": "Enjoy!"
          }
        ],
