        "sections": [
          {
            "name": null,
            "components": [
              {
                "raw_text": "2 tablespoons vegetable oil"
              },
              {
                "raw_text": "1 small white onion, diced"
              },
              {
                "raw_text": "1 stalk celery, diced"
              },
              {
                "raw_text": "1 small apple, diced"
              },
              {
                "raw_text": "2 teaspoons kosher salt"
              },
              {
                "raw_text": "1 teaspoon pepper"
              },
              {
                "raw_text": "1 tablespoon chopped fresh parsley"
              },
              {
                "raw_text": "1 tablespoon chopped fresh rosemary leaves"
              },
              {
                "raw_text": "½ tablespoon chopped fresh thyme leaves"
              },
              {
                "raw_text": "1 teaspoon dried sage"
              },
              {
                "raw_text": "1½ loaves of white bread, cubed"
              },
              {
                "raw_text": "5 cups low-sodium vegetable broth"
              }
            ]
          }
        ],
        "instructions": [
          {
            "display_text": "Preheat the oven to 350°F (180°C)."
          },
          {
            "display_text": "Heat the oil in a medium pot over medium-high heat. Once the oil begins to shimmer, add the onion and celery and cook for 5-6 minutes, until the onion is semi-translucent and the celery has softened a bit."
          },
          {
            "display_text": "Add the apple and cook for an additional 4-5 minutes, until softened."
          },
          {
            "display_text": "Add the salt, pepper, parsley, rosemary, thyme, and sage and cook for 2-3 minutes, until the herbs are fragrant."
          },
          {
            "display_text": "Add in about a ¼ of the cubed bread and 1 cup (240 ml) of the vegetable broth."
          },
          {
            "display_text": "Stir well until bread cubes cook down. Continue, alternating between adding bread and broth, until everything has cooked down but the stuffing is still slightly chunky."
          },
          {
            "display_text": "Transfer to a large baking dish and cover with foil."
          },
          {
            "display_text": "Bake for 10 minutes, then uncover and bake for another 10 minutes, until the top is golden brown. Serve warm."
          },
          {
            "display_text": "Enjoy!"
          }
        ],
